import React, { useState } from 'react'
import { useDispatch, useSelector } from "react-redux";
import {editContact} from '../actions/studentsActions'
const UpdateStudent = () => {
  const [city, setCity] = useState('')
  const [fName, setFName] = useState('')
  const [isEdit, setIsEdit] = useState(false)
  const studentsList = useSelector(state => state.students)
  const dispatch = useDispatch()
  const handleSubmit = (e) => {
    e.preventDefault()
    let editedContact = {
      city,
      fName
    }
    dispatch(editContact(editedContact))
    setCity('')
    setFName('')
  }
  return (
    <>
      {/* <ul>
      {studentsList.map(students => {
          <li>{students.fName} {students.city} <a href='#' onClick={()=>setIsEdit(true)}>edit</a>
            {isEdit ? <form onSubmit={(e)=>handleSubmit(e)}>
              <input type='text' onChange={(e)=>setFName(e.target.value)}>{studentsList.fName}</input>
              <input type='text' onChange={(e)=>setCity(e.target.value)}>{studentsList.city}</input>
              <button variant="primary" type="submit">
                Submit
              </button>
            </form> :'' }
            </li>

      })}

      </ul> */}
      <ul>
        {studentsList.map(student => {
          return <li key={student.id}>{student.fName} <br />{student.city}
            <a href='#' onClick={(student) => handleEdit(student)}>edit</a>

          </li>

        })}
      </ul> 
      {isEdit ? <form onSubmit={(e) => handleSubmit(e)}>
        <input type='text' onChange={(e) => setFName(e.target.value)} ></input>
        <input type='text' onChange={(e) => setCity(e.target.value)} ></input>
        <button variant="primary" type="submit">
          Submit
        </button>
      </form> : ''}
    </>
  )
}

export default UpdateStudent
