import React, { useState } from 'react'
import { useDispatch, useSelector } from "react-redux";
import {editContact} from '../actions/studentsActions'
const UpdateStudent = () => {
  const [city, setCity] = useState('')
  const [fName, setFName] = useState('')
  const [isEdit, setIsEdit] = useState(false)
  const studentsList = useSelector(state => state.students)
  const dispatch = useDispatch()
  const handleSubmit = (e) => {
    e.preventDefault()
    let editedContact = {
      city,
      fName
    }
    dispatch(editContact(editContact))
    setCity('')
    setFName('')
  }
  return (
    <>
      <ul>
      {studentsList.map(students => {
        return (
        <>
            <li>{students.fName} {students.city}</li> <a href='#' onClick={setIsEdit(true)}>edit</a>
            {isEdit ? <form onSubmit={(e)=>handleSubmit(e)}>
              <input type='text' onChange={(e)=>setFName(e.target.value)}>{studentsList.fName}</input>
              <input type='text' onChange={(e)=>setCity(e.target.value)}>{studentsList.city}</input>
              <button variant="primary" type="submit">
                Submit
              </button>
            </form> :'' }

        </>)
      })}

      </ul>
    </>
  )
}

export default UpdateStudent
